import {
    AppBar, Box, Container,
    FormControl,
    FormControlLabel,
    FormLabel, Grid, IconButton, InputBase, Paper,
    Radio,
    RadioGroup,
    TextField, Toolbar, Typography
} from '@material-ui/core';
import { makeStyles } from '@material-ui/styles';
import { VirtualizedList } from '../components/VirtualizedList';
import React, { useState, useEffect } from 'react';
import { ResultRenderer } from '../components/ResultRenderer';
import { useDebounce } from '../utils/debounce';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        '& > *': {
            // @ts-ignore
            width: '25ch',
        },
    },
    menuButton: {
        marginRight: '1em'
    },
    title: {
        flexGrow: 1,
        display: 'none',
    },
    paper: {
        padding: '1em',
        textAlign: 'center',
        color: '#333'
    },
    search: {
        position: 'relative',
        borderRadius: 20,
        backgroundColor: '#eee',
        '&:hover': {
            backgroundColor: '#ddd',
        },
        marginLeft: 0,
        width: '100%',
    },
    searchIcon: {
        padding: '0 1em',
        height: '100%',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: '#234599'
    },
    inputRoot: {
    },

    inputInput: {
        padding: '1em 1em 1em 0',
        // vertical padding + font size from searchIcon
        paddingLeft: `calc(1em + 25px)`,
        width: '100%',
    },
}));

const Index = () => {
    const classes = useStyles();
    const [type, setType] = useState('spells');
    const [query, setQuery] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [data, setData] = useState({
        results: [],
        type
    });

    const debouncedSearchTerm = useDebounce(query, 500);

    const handleTypeChange = (event) => {
        setType(event.target.value);
    };

    useEffect(() => {
        (async () => {
            if (debouncedSearchTerm) {
                setIsLoading(true);
                try {

                const result = await fetch(`http://localhost:8080?query=${query}&type=${type}`);
                const data = await result.json();
                setData({
                    results: data,
                    type
                });

                } catch(err) {
                    setData({
                        results: [],
                        type
                    })
                }
                setIsLoading(false);
            } else {
            }
        })()
    }, [debouncedSearchTerm, type]);

    return (
        <Container maxWidth="xl">
            <Grid container spacing={1}>
                <Grid item xs={12} md={3}>
                    <Paper className={classes.paper}>
                        <Box m={2}>
                            <div className={classes.search}>
                                <div className={classes.searchIcon}>
                                    <SearchIcon />
                                </div>
                                <InputBase
                                    placeholder="Searchâ€¦"
                                    onChange={(e) => setQuery(e.target.value)}
                                    classes={{
                                        root: classes.inputRoot,
                                        input: classes.inputInput,
                                    }}
                                    inputProps={{ 'aria-label': 'search' }}
                                />
                            </div>
                        </Box>
                        <FormControl component="fieldset">
                            <FormLabel component="legend">Query Type</FormLabel>
                            <RadioGroup aria-label="Query Type" name="gender1" value={type} onChange={handleTypeChange}>
                                <FormControlLabel value="feats" control={<Radio />} label="Feats" />
                                <FormControlLabel value="monsters" control={<Radio />} label="Monsters" />
                                <FormControlLabel value="spells" control={<Radio />} label="Spells" />
                                <FormControlLabel value="items" control={<Radio />} label="Items" />
                            </RadioGroup>
                        </FormControl>
                    </Paper>
                </Grid>
                <Grid item xs={12} md={9}>
                    <div style={{maxWidth: '1024px', margin: '0 auto'}}>
                        {isLoading ? (
                            <div>Loading ...</div>
                        ) : (
                            <VirtualizedList itemData={data} />
                        )}
                    </div>
                </Grid>
            </Grid>
        </Container>

    )
}

export default Index;
