import {
    AppBar,
    FormControl,
    FormControlLabel,
    FormLabel, IconButton, InputBase,
    Radio,
    RadioGroup,
    TextField, Toolbar, Typography
} from '@material-ui/core';
import { makeStyles } from '@material-ui/styles';
import React, { useState, useEffect } from 'react';
import { useDebounce } from '../utils/debounce';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        '& > *': {
            // @ts-ignore
            margin: '1em',
            width: '25ch',
        },
    },
    menuButton: {
        marginRight: '1em'
    },
    title: {
        flexGrow: 1,
        display: 'none',
    },
    search: {
        position: 'relative',
        borderRadius: 20,
        backgroundColor: '#eee',
        '&:hover': {
            backgroundColor: '#ddd',
        },
        marginLeft: 0,
        width: '100%',
    },
    searchIcon: {
        padding: '0 1em',
        height: '100%',
        position: 'absolute',
        pointerEvents: 'none',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    inputRoot: {
    },

    inputInput: {
        padding: '1em 1em 1em 0',
        // vertical padding + font size from searchIcon
        paddingLeft: `calc(1em + 25px)`,
        width: '100%',
    },
}));

const Index = () => {
    const classes = useStyles();
    const [type, setType] = useState('spells');
    const [query, setQuery] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [data, setData] = useState([]);

    const debouncedSearchTerm = useDebounce(query, 500);

    const handleTypeChange = (event) => {
        setType(event.target.value);
    };

    useEffect(() => {
        (async () => {
            if (debouncedSearchTerm) {
                setIsLoading(true);
                const result = await fetch(`http://localhost:8080?query=${query}&type=${type}`);
                setData(await result.json());
                setIsLoading(false);
            } else {
                setData([{name: 'no results'}])
            }
        })()
    }, [debouncedSearchTerm, type]);

    return (
        <div>
            <AppBar position="static">
                <Toolbar>
                    <IconButton
                        edge="start"
                        className={classes.menuButton}
                        color="inherit"
                        aria-label="open drawer"
                    >
                        <MenuIcon />
                    </IconButton>
                    <Typography className={classes.title} variant="h6" noWrap>
                        Material-UI
                    </Typography>
                    <div className={classes.search}>
                        <div className={classes.searchIcon}>
                            <SearchIcon />
                        </div>
                        <InputBase
                            placeholder="Search…"
                            onChange={(e) => setQuery(e.target.value)}
                            classes={{
                                root: classes.inputRoot,
                                input: classes.inputInput,
                            }}
                            inputProps={{ 'aria-label': 'search' }}
                        />
                    </div>
                </Toolbar>
            </AppBar>







            <form className={classes.root} noValidate autoComplete="off">
                <TextField
                    id="outlined-basic"
                    label="Outlined"
                    variant="outlined"
                    onChange={(e) => setQuery(e.target.value)}
                />
            </form>
            <FormControl component="fieldset">
                <FormLabel component="legend">Gender</FormLabel>
                <RadioGroup aria-label="gender" name="gender1" value={type} onChange={handleTypeChange}>
                    <FormControlLabel value="feats" control={<Radio />} label="Female" />
                    <FormControlLabel value="monsters" control={<Radio />} label="Male" />
                    <FormControlLabel value="spells" control={<Radio />} label="Other" />
                    <FormControlLabel value="items" disabled control={<Radio />} label="(Disabled option)" />
                </RadioGroup>
            </FormControl>
            <div>
                {isLoading ? (
                    <div>Loading ...</div>
                ) : (
                    <ul>
                        {
                        data.map((datum) => {
                            return (
                                <li>{datum.name}</li>
                            )
                        })
                        }
                    </ul>
                )}
            </div>
        </div>

    )
}

export default Index;
