import React, { useState, useEffect } from 'react';
import { useDebounce } from '../utils/debounce';

const Index = () => {
    const [type, setType] = useState('spells');
    const [query, setQuery] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [data, setData] = useState([]);

    const debouncedSearchTerm = useDebounce(query, 500);

    useEffect(() => {
        (async () => {
            if (debouncedSearchTerm) {
                setIsLoading(true);
                const result = await fetch(`http://localhost:8080?query=${query}&type=${type}`);
                setData(await result.json());
                setIsLoading(false);
            } else {
                setData([{name: 'no results'}])
            }
        })()
    }, [debouncedSearchTerm, type]);
    return (
        <div>
            <input type="text" onChange={(e) => setQuery(e.target.value)}/>
            <ul style={{}}>
                <li style={{color: type == 'spells' ? 'blue' : '#333'}} onClick={() => setType('spells')}>spells</li>
                <li onClick={() => setType('feats')}>feats</li>
                <li onClick={() => setType('monsters')}>monsli</li>
                <li onClick={() => setType('items')}>items</li>
            </ul>
            <div>
                {isLoading ? (
                    <div>Loading ...</div>
                ) : (
                    <ul>
                        {
                        data.map((datum) => {
                            return (
                                <li>{datum.name}</li>
                            )
                        })
                        }
                    </ul>
                )}
            </div>
        </div>

    )
}

export default Index;
